define('data-input',[
  'angular',
  'comma-separated-values',
  'text!../partials/data-forms/structure-data-input.html',
  ], function(angular, CSV, dataFormTemplate) {
    

    angular.module('chartbuilderDirectives').
      directive('structureDataInput', ['chartbuilderUtils', 'chartbuilderData', function(chartbuilderUtils, chartbuilderData) {
        return {
          restrict: 'EA',
          scope: {
            structureData: '=',
            expandData: '@?'
          },
          template: dataFormTemplate,
          link: function(scope) {
            scope.dataGroupBox = {};
            scope.newRow = {};
            scope.newDataFile = {};
            scope.singleSeriesData = {};

            // Set CSV parsing options
            var csvOptions = {
              header: true,
              line: '\n'
            };

            // Keep an empty new row for the input model
            scope.$watch('structureData.data', function(newval) {
              angular.forEach(newval, function(group, gidx) {
                scope.newRow[gidx] = {};
              });
            }, true);

            // When data is pasted to the input text area, parse it for values
            scope.onPasteInputChanged = function(input, gidx) {
              scope.structureData.data[gidx].values = new CSV(input, csvOptions).parse();
              scope.pasteInputToggle = false;
            };

            // Append a new data row from the empty data model
            scope.addRow = function(gidx) {
              var validate = true;
              angular.forEach(scope.structureData.columnValues, function(type, key) {
                if (!_.has(scope.newRow[gidx], key) || scope.newRow[gidx][key] === null) {
                  validate = false;
                }
              });
              if (!validate) {
                return false;
              }
              scope.structureData.data[gidx].values.push(scope.newRow[gidx]);
            };

            scope.addGroup = function() {
              if (!scope.newDataGroup) {
                return false;
              }
              chartbuilderData.addGroup(scope.newDataGroup);
              scope.newDataGroup = '';
            };

            scope.duplicateGroup = function() {
              if (!scope.newDataGroup) {
                return false;
              }
              chartbuilderData.duplicateGroup(scope.newDataGroup);
              scope.newDataGroup = '';
            };

            scope.removeRow = function(gidx, idx) {
              scope.structureData.data[gidx].values.splice(idx, 1);
            };

            // This is just the callback from the file reader input. Parse and insert uploaded values
            scope.readFile = function(file, gidx) {
              scope.structureData.data[gidx].values = new CSV(file, csvOptions).parse();
            };

            // Download existing csv data
            scope.downloadCSV = function(gidx) {
              var csvText = new CSV(scope.structureData.data[gidx].values, csvOptions).encode();
              chartbuilderUtils.saveFile(csvText, scope.structureData.data[gidx].key + '.csv', 'text/csv');
            };
          }
        };
      }]);
});


define('text!../partials/data-forms/edit-in-place.html',[],function () { return '<span ng-click="edit()" ng-bind="value"></span>\n<input ng-model="value" type="{{ type }}" class="form-control"></input>\n';});

define('edit-in-place',[
  'angular',
  'text!../partials/data-forms/edit-in-place.html',
  ], function(angular, editInPlaceTemplate) {
    

    angular.module('chartbuilderDirectives')
      .directive('editInPlace', function() {
        return {
          restrict: 'EA',
          scope: {
            value: '=',
            type: '@?'
          },
          template: editInPlaceTemplate,
          link: function(scope, element) {
            var inputElement = angular.element(element.children()[1]);

            element.addClass('edit-in-place');

            scope.editing = false;

            scope.edit = function() {
              scope.editing = true;

              element.addClass('active');
              inputElement[0].focus();
            };

            inputElement.bind('keydown keypress', function(event) {
              if(event.which === 13) {
                scope.editing = false;
                element.removeClass('active');
              }
            });

            inputElement.bind('blur', function() {
              scope.editing = false;
              element.removeClass('active');
            });
          }
        };
      });
});

define('template-loader',[
  'angular',
  ], function(angular) {
    

    function isJson(data) {
      try {
        return angular.fromJson(data);
      } catch(e) {
        console.log('Not a valid JSON object');
        return false;
      }
    }

    function parseDataForWP(data) {
      if (angular.isUndefined(data) || angular.isUndefined(data.options.chart) || angular.isUndefined(data.options.chart.type)) {
        alert('You must select a chart type and add data before sending to WordPress');
        return false;
      }

      // validate chart data
      if (!isJson(data)) {
        return false;
      }

      // validate chart type
      if (!angular.isString(data.options.chart.type)) {
        console.log('invalid chart type');
        return false;
      }

      // Unset preloaded for loading
      delete scope.chartbuilderData.preloaded;

      // return chart data
      return angular.toJson(data);
    }

    angular.module('chartbuilderDirectives')
      .directive('chartOptionsLoader', function() {
        return {
          restrict: 'EA',
          replace: true,
          template: '<button type="button" class="btn btn-default btn-file-input" file-input-button on-file-load="readTemplateFile(file)" name="Load chart object">load</button>',
          link: function(scope) {
            // Get the file from the file-input directive, make sure it's json
            scope.readTemplateFile = function(file) {
              var optionsObject = isJson(file);

              if (optionsObject) {
                scope.chartbuilderData.load(optionsObject);
              }

            };
          }
        }
      })
      .directive('chartTemplateOptionsLoader', function() {
        return {
          restrict: 'EA',
          replace: true,
          template: '<button type="button" class="btn btn-default btn-file-input" file-input-button on-file-load="readTemplateFile(file)" name="Upload Template Options">load options</button>',
          link: function(scope) {
            // Get the file from the file-input directive, make sure it's json
            scope.readTemplateFile = function(file) {
              var optionsObject = isJson(file);

              if (optionsObject) {
                scope.chartbuilderData.loadOptions(optionsObject);
              }

            };
          }
        }
      })
      .directive('chartOptionsSaver', ['chartbuilderUtils', function(chartbuilderUtils) {
        return {
          restrict: 'EA',
          link: function(scope, element, attrs) {
            // Download the current chartbuilderData object
            scope.downloadOptionsObject = function() {
              var optionsObject = scope.getOptions();
              chartbuilderUtils.saveFile(optionsObject, 'chartbuilder-options.json', 'text/json');
            };

            scope.getOptions = function() {
              if (!attrs.optionsOnly) {
                return angular.toJson(scope.chartbuilderData);
              } else {
                var templateOptions = {
                  colors: scope.chartbuilderData.colors,
                  options: scope.chartbuilderData.options.chart
                }
                return angular.toJson(templateOptions);
              }
            };
          }
        }
      }])
      .directive('chartOptionsFromWindow', ['$window', function($window) {
        return {
          restrict: 'EA',
          replace: true,
          template: '<button type="button" class="btn btn-default" ng-click="sendToWordPress()" name="Send Options Object to Wordpress">to WP</button>',
          link: function(scope) {

            /**
             * WordPress plugin integration functions
             */

            // this is initialized on the directive
            scope.initDataLoad = function() {
              // // if bootstrapped data, use that
              // // i.e. front-end of site
              // if ( !angular.isUndefined( $window.chartbuilderOptions ) && $window.chartbuilderOptions) {
              //   scope.chartbuilderData.load($window.chartbuilderOptions);
              //   return;
              // }

              // confirm that we're in an iframe
              if ( ! window.frameElement ){
                return;
              }

              // setup postMessage listener and tell parent window that child frame is ready to receive data
              var origin = $window.location.protocol + '//' + $window.location.hostname;              
              window.addEventListener('message', scope.receiveMessage, true);
              window.parent.postMessage( {
                src : 'chartbuilder',
                channel : 'upstream',
                msg : 'ready',
                data : null
              }, origin )

            };

            scope.receiveMessage = function(e){
              if ( e.origin !== $window.location.protocol + '//' + $window.location.hostname ){
                throw( 'Illegal postMessage from ' + e.origin );
              }

              var msgObj = e.data;
              if (  !angular.isUndefined( msgObj.src) &&
                    msgObj.src === 'chartbuilder' &&
                    !angular.isUndefined( msgObj.channel ) &&
                    msgObj.channel === 'downstream' &&
                    !angular.isUndefined( msgObj.msg ) &&
                    msgObj.msg === 'savedData' &&
                    !angular.isUndefined( msgObj.data ) &&
                    msgObj.data
              ) {
                console.log( 'App iframe received savedData from WordPress')
                console.log( msgObj.data );
                scope.chartbuilderData.env = 'iframe';
                scope.chartbuilderData.load( msgObj.data );
              }
            };

            scope.initDataLoad();

            scope.sendToWordPress = function(){

              var chartData = parseDataForWP(scope.chartbuilderData);

              $window.parent.postMessage({
                src : 'chartbuilder',
                channel : 'upstream',
                msg : 'chartData',
                data : chartData,
              }, $window.location.href);
            }
          }
        };
      }]);
});

